INSERT INTO `email` VALUES (1,'Gmail Team','<!DOCTYPE html>\r\n<html><head><meta http-equiv=\"content-type\" content=\"text/html;charset=UTF-8\"/><title>The best of Gmail, wherever you are</title></head><body style=\"background-color:#e5e5e5; margin:20px 0;\"><br/><div style=\"margin:2%;\"><div style=\"direction:ltr; text-align:left; font-family:\'Open sans\',\'Arial\',sans-serif; color:#444; background-color:white; padding:1.5em; border-radius:1em; box-shadow:1px -5px 8px 2px #bbb; max-width:580px; margin:2% auto 0 auto;\"><table style=\"background:white;width:100%\"><tr><td><div style=\"width:90px; height:54px; margin:10px auto;\"><img src=\"https://services.google.com/fh/files/emails/google_logo_flat_90_color.png\" alt=\"Google\" width=\"90\" height=\"34\"/></div><div style=\"float:right; padding-top:2em;\"><img src=\"https://ssl.gstatic.com/accounts/services/mail/msa/welcome_nexus.png\" alt=\"Nexus 4 with Gmail\" style=\"border:0; margin-right:10px;\" width=\"155\" height=\"242\"/></div><div style=\"width:90%; padding-bottom:10px; padding-left:15px\"><p><img alt=\"\" aria-hidden=\"true\" src=\"https://ssl.gstatic.com/ui/v1/icons/mail/images/gmail_logo_large.png\" style=\"display:inline-block; max-height:10px; margin-right:5px;\"/><span style=\"font-family:\'Open sans\',\'Arial\',sans-serif; font-weight:bold; font-size:small; line-height:1.4em\">Hi Kevin</span></p><p><span style=\"font-family:\'Open sans\',\'Arial\',sans-serif; font-size:2.08em;\"><br/>Get the official Gmail app</span><br/></p></div><p></p><div style=\"padding-left:15px\"><p style=\"size:small; line-height:1.4em;\">The best features of Gmail are only available on your phone and tablet with the official Gmail app. Download the app or go to <a href=\"https://www.gmail.com/\" target=\"_blank\" style=\"text-decoration:none; color:#15C\">gmail.com</a> on your computer or mobile device to get started.</p><p style=\"line-height:2em; margin-right:170px;\"><a href=\"https://play.google.com/store/apps/details?id=com.google.android.gm\" style=\"text-decoration:none\"><img alt=\"Google Play\" width=\"127\" height=\"44\" src=\"https://ssl.gstatic.com/accounts/services/mail/buttons/google_play_en.png\" style=\"border:0\"/></a>&nbsp;&nbsp;<a href=\"https://itunes.apple.com/en/app/gmail/id422689480?mt=8\" style=\"text-decoration:none;\"><img alt=\"App Store\" width=\"144\" height=\"43\" src=\"https://ssl.gstatic.com/accounts/services/mail/buttons/apple_store_en.png\" style=\"border:0\"/></a></p></div><br/><br/>\r\n<div style=\"clear:both; padding-left:13px; height:6.8em;\"><table style=\"width:100%; border-collapse:collapse; border:0\"><tr><td style=\"width:68px\"><img alt=\'Gmail icon\' width=\"49\" height=\"37\" src=\"https://ssl.gstatic.com/ui/v1/icons/mail/images/gmail_logo_large.png\" style=\"display:block;\"/></td><td style=\"align:left; font-family:\'Open sans\',\'Arial\',sans-serif; vertical-align:bottom\"><span style=\"font-size:small\">Happy emailing,<br/></span><span style=\"font-size:x-large; line-height:1\">The Gmail Team</span></td></tr></table></div>\r\n</td></tr></table></div>\r\n<div style=\"direction:ltr;color:#777; font-size:0.8em; border-radius:1em; padding:1em; margin:0 auto 4% auto; font-family:\'Arial\',\'Helvetica\',sans-serif; text-align:center;\">© 2018 Google Inc. 1600 Amphitheatre Parkway, Mountain View, CA 94043<br/></div></div></body></html>\r\n','2018-08-20 13:33:56','2018-08-20 13:33:56','The best of Gmail, wherever you are',0,'2018-08-14 08:12:20','mail-noreply@google.com',NULL,NULL,NULL),(2,'Gmail Team','<!DOCTYPE html>\r\n<html><head><meta http-equiv=\"content-type\" content=\"text/html;charset=UTF-8\"/><title>Tips for using your new inbox</title></head><body style=\"background-color:#e5e5e5; margin:20px 0;\"><br/><div style=\"margin:2%;\"><div style=\"direction:ltr; text-align:left; font-family:\'Open sans\',\'Arial\',sans-serif; color:#444; background-color:white; padding:1.5em; border-radius:1em; box-shadow:1px -5px 8px 2px #bbb; max-width:580px; margin:2% auto 0 auto;\"><table style=\"background:white;width:100%\"><tr><td><div style=\"width:90px; height:54px; margin:10px auto;\"><img src=\"https://services.google.com/fh/files/emails/google_logo_flat_90_color.png\" alt=\"Google\" width=\"90\" height=\"34\"/></div><div style=\"width:90%; padding-bottom:10px; padding-left:15px\"><p><img alt=\"\" aria-hidden=\"true\" src=\"https://ssl.gstatic.com/ui/v1/icons/mail/images/gmail_logo_large.png\" style=\"display:inline-block; max-height:10px; margin-right:5px;\"/><span style=\"font-family:\'Open sans\',\'Arial\',sans-serif; font-weight:bold; font-size:small; line-height:1.4em\">Hi Kevin</span></p><p><span style=\"font-family:\'Open sans\',\'Arial\',sans-serif; font-size:2.08em;\">Welcome to your Gmail inbox</span><br/></p></div><p></p><div style=\"float:left; clear:both; padding:0px 5px 10px 10px;\"><img src=\"https://ssl.gstatic.com/accounts/services/mail/msa/welcome_storage.png\" alt=\"Storage\" style=\"display:block;\" width=\"129\" height=\"129\"/></div><div style=\"float:left; vertical-align:middle; padding:10px; max-width:398px; float:left;\"><table style=\"vertical-align:middle;\"><tr><td style=\"font-family:\'Open sans\',\'Arial\',sans-serif;\"><span style=\"font-size:20px;\">Save everything</span><br/><br/><span style=\"font-size:small; line-height:1.4em\">With tons of storage space, you’ll never need to delete an email. Just keep everything and easily find it later.</span></td></tr></table></div><div style=\"float:left; clear:both; padding:0px 5px 10px 10px;\"><img src=\"https://ssl.gstatic.com/mail/welcome/localized/en/welcome_search.png\" alt=\"Search\" style=\"display:block;\" width=\"129\" height=\"129\"/></div><div style=\"float:left; vertical-align:middle; padding:10px; max-width:398px; float:left;\"><table style=\"vertical-align:middle;\"><tr><td style=\"font-family:\'Open sans\',\'Arial\',sans-serif;\"><span style=\"font-size:20px;\">Find emails fast</span><br/><br/><span style=\"font-size:small; line-height:1.4em\">With the power of Google Search right in your inbox, you can quickly find the important emails you need with suggestions based on your emails, past searches and contacts.</span></td></tr></table></div><div style=\"float:left; clear:both; padding:0px 5px 10px 10px;\"><img src=\"https://ssl.gstatic.com/mail/welcome/localized/en/welcome_tabs.png\" alt=\"Inbox\" style=\"display:block;\" width=\"129\" height=\"129\"/></div><div style=\"float:left; vertical-align:middle; padding:10px; max-width:398px; float:left;\"><table style=\"vertical-align:middle;\"><tr><td style=\"font-family:\'Open sans\',\'Arial\',sans-serif;\"><span style=\"font-size:20px;\">Arrange your inbox to fit your work style</span><br/><br/><span style=\"font-size:small; line-height:1.4em\">Choose to see <em>unread</em>, <em>starred</em> or <em>important</em> messages at the top of your inbox or try <a href=\"https://support.google.com/mail/answer/186531?hl=en&amp;utm_source=gmailwelcomeemail&amp;utm_medium=email&amp;utm_campaign=gmailwelcome\" style=\"text-decoration:none; color:#15C\">Priority Inbox</a>, which combines all of these. Go to the <a href=\"https://mail.google.com/mail/#settings/inbox\" style=\"text-decoration:none; color:#15C\">Inbox tab in Gmail settings to make changes</a>.</span></td></tr></table></div><div style=\"float:left; clear:both; padding:0px 5px 10px 10px;\"><img src=\"https://ssl.gstatic.com/accounts/services/mail/msa/welcome_hangouts_2.png\" alt=\"Video meetings\" style=\"display:block;\" width=\"129\" height=\"129\"/></div><div style=\"float:left; vertical-align:middle; padding:10px; max-width:398px; float:left;\"><table style=\"vertical-align:middle;\"><tr><td style=\"font-family:\'Open sans\',\'Arial\',sans-serif;\"><span style=\"font-size:20px;\">Send messages and hold video meetings from your inbox</span><br/><br/><span style=\"font-size:small; line-height:1.4em\">Chat with contacts and start video meetings with up to 25 people in <a href=\"https://support.google.com/hangouts/answer/3115176?hl=en&amp;utm_source=gmailwelcomeemail&amp;utm_medium=email&amp;utm_campaign=gmailwelcome#hangouts\" style=\"text-decoration:none; color:#15C\">Google Hangouts</a>.</span></td></tr></table></div><div style=\"float:left; clear:both; padding:0px 5px 10px 10px;\"><img src=\"https://services.google.com/fh/files/emails/importcontacts.png\" alt=\"Contacts\" style=\"display:block;\" width=\"129\" height=\"129\"/></div><div style=\"float:left; vertical-align:middle; padding:10px; max-width:398px; float:left;\"><table style=\"vertical-align:middle;\"><tr><td style=\"font-family:\'Open sans\',\'Arial\',sans-serif;\"><span style=\"font-size:20px;\">Bring your contacts into Gmail</span><br/><br/><span style=\"font-size:small; line-height:1.4em\">You can <a href=\"https://support.google.com/a/answer/14024?hl=en&amp;topic=3056079&amp;utm_source=gmailwelcomeemail&amp;utm_medium=email&amp;utm_campaign=gmailwelcome\" style=\"text-decoration:none; color:#15C\">import your contacts</a> from other webmail to make the transition to Gmail easier. <a href=\"https://support.google.com/a/answer/14024?hl=en&amp;topic=3056079&amp;utm_source=gmailwelcomeemail&amp;utm_medium=email&amp;utm_campaign=gmailwelcome\" style=\"text-decoration:none; color:#15C\">Learn how</a></span></td></tr></table></div><br/><br/>\r\n<div style=\"clear:both; padding-left:13px; height:6.8em;\"><table style=\"width:100%; border-collapse:collapse; border:0\"><tr><td style=\"width:68px\"><img alt=\'Gmail icon\' width=\"49\" height=\"37\" src=\"https://ssl.gstatic.com/ui/v1/icons/mail/images/gmail_logo_large.png\" style=\"display:block;\"/></td><td style=\"align:left; font-family:\'Open sans\',\'Arial\',sans-serif; vertical-align:bottom\"><span style=\"font-size:small\">Happy emailing,<br/></span><span style=\"font-size:x-large; line-height:1\">The Gmail Team</span></td></tr></table></div>\r\n</td></tr></table></div>\r\n<div style=\"direction:ltr;color:#777; font-size:0.